#!/usr/bin/env python3
"""
Simple Loss Plotting for DINO Training
Real-time loss visualization - short and sweet!
"""

import matplotlib.pyplot as plt
import numpy as np
from collections import deque
import json
import time

class LossPlotter:
    def __init__(self, save_path="loss_plot.png", max_points=1000):
        self.losses = deque(maxlen=max_points)
        self.epochs = deque(maxlen=max_points)
        self.batch_steps = deque(maxlen=max_points)
        self.save_path = save_path
        
        # Setup plot
        plt.ion()  # Interactive mode
        self.fig, self.ax = plt.subplots(figsize=(12, 6))
        self.line, = self.ax.plot([], [], 'b-', linewidth=1.5, alpha=0.8)
        self.ax.set_xlabel('Training Steps')
        self.ax.set_ylabel('Loss')
        self.ax.set_title('DINO Training Loss')
        self.ax.grid(True, alpha=0.3)
        
    def update(self, epoch, batch, loss, total_batches_per_epoch):
        """Update plot with new loss value"""
        # Calculate global step
        global_step = epoch * total_batches_per_epoch + batch
        
        # Add data
        self.losses.append(loss)
        self.epochs.append(epoch)
        self.batch_steps.append(global_step)
        
        # Update plot
        self.line.set_data(list(self.batch_steps), list(self.losses))
        
        # Auto-scale
        if len(self.losses) > 1:
            self.ax.relim()
            self.ax.autoscale()
        
        # Add epoch markers
        if batch == 0 and len(self.losses) > 1:
            self.ax.axvline(x=global_step, color='red', linestyle='--', alpha=0.5)
            self.ax.text(global_step, max(self.losses), f'E{epoch}', 
                        rotation=90, verticalalignment='bottom')
        
        # Refresh
        self.fig.canvas.draw()
        self.fig.canvas.flush_events()
        
        # Save periodically
        if batch % 100 == 0:
            self.save()
    
    def save(self):
        """Save current plot"""
        self.fig.savefig(self.save_path, dpi=150, bbox_inches='tight')
        
        # Also save data as JSON
        data = {
            'losses': list(self.losses),
            'epochs': list(self.epochs), 
            'batch_steps': list(self.batch_steps)
        }
        with open(self.save_path.replace('.png', '_data.json'), 'w') as f:
            json.dump(data, f)


