# Quick standalone version - parse log v√† t·∫°o plot ngay
def plot_from_log(log_text):
    """Parse log text v√† t·∫°o loss plot ngay"""
    import re
    import matplotlib.pyplot as plt
    
    # Extract loss values t·ª´ log
    pattern = r'Batch (\d+)/(\d+), Loss: ([\d.]+)'
    matches = re.findall(pattern, log_text)
    
    if not matches:
        print("Kh√¥ng t√¨m th·∫•y loss values trong log!")
        return
    
    # Parse data
    batches = []
    losses = []
    
    for batch, total_batches, loss in matches:
        batches.append(int(batch))
        losses.append(float(loss))
    
    # Create plot
    plt.figure(figsize=(12, 6))
    plt.plot(batches, losses, 'b-', linewidth=1.5, alpha=0.8)
    plt.xlabel('Batch')
    plt.ylabel('Loss')
    plt.title('DINO Training Loss Progress')
    plt.grid(True, alpha=0.3)
    
    # Add trend line
    z = np.polyfit(batches, losses, 1)
    p = np.poly1d(z)
    plt.plot(batches, p(batches), "r--", alpha=0.8, label=f'Trend (slope: {z[0]:.4f})')
    plt.legend()
    
    # Stats
    total_drop = losses[0] - losses[-1]
    plt.text(0.02, 0.98, f'Loss drop: {total_drop:.3f}\nStart: {losses[0]:.3f}\nCurrent: {losses[-1]:.3f}', 
             transform=plt.gca().transAxes, verticalalignment='top',
             bbox=dict(boxstyle='round', facecolor='wheat', alpha=0.8))
    
    plt.tight_layout()
    plt.savefig('dino_loss_plot.png', dpi=150, bbox_inches='tight')
    plt.show()
    print("üìä Plot saved as 'dino_loss_plot.png'")


# Super simple one-liner usage
def quick_plot(log_file_path):
    """ƒê·ªçc file log v√† plot ngay"""
    with open(log_file_path, 'r', encoding="utf-8") as f:
        log_text = f.read()
    plot_from_log(log_text)


